  void print(TreeNode *root); //Helper for inorderDump;                                                                                                                                                     
  TreeNode *removeH(TreeNode*, int); //Helper for remove                                                                                                                                                    
  TreeNode *min(TreeNode*); //finds min value in a subtree. Helper for remove                                                                                                                               
