  bool rtlsH( TreeNode*, int);                                                                                                                                                                              
                                                                                                                                                                                                            
  void local_values(TreeNode*, int, int);                                                                                                                                                                   
  bool local_identical( TreeNode*, TreeNode* );                                                                                                                                                             
